// consts 

const RECEIVE_POST = "api/RECEIVE_POST";
const RECEIVE_POSTS = "api/RECEIVE_POSTS";
const REMOVE_POST = 'api/UPDATE_POST';

// action types

const receivePost = (post) => {
    type: RECEIVE_POST,
    post 
}

const receivePosts = (posts) => {
    type: RECEIVE_POSTS,
    posts
}

const deletePost = (postId) =>{
    type: REMOVE_POST,
    postId
}
