}


const Modal = ({onClose,children})=>{
    const modalNode = useContext();

    return(
        <div id='modal'>
            <div id='modal-background' onClick={()=>onClose()}></div>
            <div id='modal-content'>
                {children}
            </div>
        </div>
    )
}

export default ModalContext;
