import { useEffect,useState } from "react"
import { Link } from "react-router-dom";
const PostCircle = ({type}) =>{
    const [isHovered,setIsHovered] = useState(false);
    const [decorators,setDecorators]=useState({
        color: '',
        icon: '',
        text:''
    })
    // i need a color
    // text
    // link to
    // icon
    //need on mouseEnter and onMouseLeave functions
    // used to add animation to the icons
    useEffect(()=>{
        switch (type) {
            case "text":
                setDecorators({
                    color:'',
                    icon: 'fa-solid fa-font-case',
                    text: 'Text'
                }
                )
                break;
            case "photo":
                decorators.color="";
                decorators.icon='fa-solid fa-camera-retro';
                decorators.text='Photo';
                break;
            default:
                break;
        }
    },[])
    console.log(decorators);
    return(
        <div className='selection-container'>
                    <div className='selection-body'>
                    <Link className='slection-link' to='/new/text'>
                        <div className='selection-button' style={{backgroundColor: decorators.color}}>
                            <i className={decorators.icon} style={{backgroundC:'red'}}></i>
                            <div className='selection-text'>{decorators.text}</div>
                        </div>
                    </Link>
                    </div>
        </div>
    )

}

export default PostCircle;
