import './styling/posttext.css';
import { useState } from 'react';
const PostText = ({post}) => {
    // need to check if post has a title
    // if post has a title, then that will be the first child of post-paragraph and have bigger font
    const [photoCount,setPhotoCount]=useState(1);
    const styles = {
        fontSize: '1.625rem',
        lineHeight: 1.3077,
        paddingLeft: '20px',
        paddingRight: '20px',
        minHeight: '1.5em',
        marginBottom: '15px',
      };
    console.log(post);
    const breakdown = (body, title) => {
        const array = body.split('\n');
        console.log(array);
        return array.map((sentence,index)=>{
            console.log(sentence==='_@#$photo__@#$')
            if(sentence==='_@#$photo__@#$'){
                switch (photoCount) {
                    case 1:
                        setPhotoCount(2);
                        return(
                            <div className='image-container'>
                                <figure className='image-figure'>
                                    <div className='image-box'>
                                        <img src={post.photo1} alt='' className='post-image' />
                                    </div>
                                </figure>
                            </div>
                        )
                    case 2:
                        setPhotoCount(3);
                        return(
                            <div className='image-container'>
                                <figure className='image-figure'>
                                    <div className='image-box'>
                                        <img src={post.photo2} alt='' className='post-image' />
                                    </div>
                                </figure>
                            </div>
                        )
                    case 3:
                        setPhotoCount(4);
                        return(
                            <div className='image-container'>
                                <figure className='image-figure'>
                                    <div className='image-box'>
                                        <img src={post.photo3} alt='' className='post-image' />
                                    </div>
                                </figure>
                            </div>
                        )
                    case 4:
                        setPhotoCount(5);
                        return(
                            <div className='image-container'>
                                <figure className='image-figure'>
                                    <div className='image-box'>
                                        <img src={post.photo4} alt='' className='post-image' />
                                    </div>
                                </figure>
                            </div>
                        )
                    default:
                        break;
                }
            }
            return(
            <div key={index} className='post-paragraph'>
                <p className='textStrings'>{sentence}</p>
                <p></p>
            </div>
            )
        })
    }

    return (
        <>
            <div className='posttext-body'>
                <p className='post-paragraph'>
                    {post.title!=='' && <h1 style={{fontSize: '1.625rem',lineHeight: 1.3077 }}>{post.title}</h1>}
                </p>
                {breakdown(post.body)}
            </div>
        </>
    )

}

export default PostText;
