import { useState, useRef } from "react";
const NewPostInput = ({handleKeyDown,index}) => {
    const [type,setType] = useState('bob')

    // I need a options menu that will only populate if type is text, and user has not typed anything into the innerTag
    //should i use a useRef that will track the innerText
    return (
        <>

            {type==='text' && <p  key={index} data-type={index} onKeyDown={(event)=>handleKeyDown(event)} id={`paragraph-${index}`}  className='contentEdit text-paragraph' contentEditable='true'>test my component</p>}
        </>
        )

}

