const PostText = ({post}) => {

    const breakdown = (string) => {
        const array = string.split('\n');
        array.map((sentence,index)=>{
            return(
            <div key={index} className='post-setence'>
                {sentence}
            </div>
            )
        })
    }

    return (
        <>
            {breakdown(post.body)}
        </>
    )

}

export default PostText;
