import { useState } from "react"
const EmailCheck = ({setEmail,onEmailClick}) =>{

    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    }
    const [initialemail,setInitialEmail] = useState();
    return (
        <>
        <input type='text' placeholder='Username or Email' 
            value={initialemail} onChange={event=>setEmail(event.target.value)} required
            />
            <button onClick={event=>onEmailClick(event)} style={{ backgroundColor: 'transparent', border: 'none', boxShadow: 'none' }}>
            <span>Next <i className="fa-solid fa-arrow-right" style={{color:'#000000'}}></i> </span>
            </button>
        </>
    )
}
export default EmailCheck;
