import './posttext.css';
const PostText = ({post}) => {

    const breakdown = (string) => {
        const array = string.split('\n');
        return array.map((sentence,index)=>{
            return(
            <div key={index} className='post-paragraph'>
                {/* <p className='textStrings'>{sentence}</p> */}
                <p> The world is healing, Rauru is great, your art blocks are being cured, and no one, absolutely no one, is thinking of the Zelda x Link ship name being Linda instead of Zelink. Tears of the Kingdom is here, and mentally, we're all in Hyrule for the foreseeable future. Here's some fanart for your heart containers. </p>
                <p></p>
            </div>
            )
        })
    }

    return (
        <>
            <div className='posttext-body'>
                {breakdown(post.body)}
            </div>
        </>
    )

}

export default PostText;
