import React from 'react';
import ReactDOM from 'react-dom';
import { Provider } from 'react-redux';
import { BrowserRouter } from 'react-router-dom';
import './index.css';
import './reset.css';
import App from './App';
import configureStore from './store';
import csrfFetch from './store/csrf';
import * as sessionActions from './store/session';
import ModalProvider from './components/Context/Modal';
import * as postActions from './store/posts';
import * as userActions from './store/user';
import { useState, useEffect } from 'react'
import { Route, Switch } from "react-router-dom"
import SignupFormPage from './components/SignupFormPage';
import Navigation from './components/Navigation';
import { useSelector } from "react-redux";
import Dashboard from './components/Dashboard/dashboard';
import TodayDashboard from './components/Dashboard/today';
import LoginFormModel from './components/LoginFormModal';
import NewPost from './components/posts/NewPost';
import NewTextModal from './components/posts/textposts/NewTextPostModal';
import NewPhotoModal from './components/posts/NewPhotoPostModal';
import UserShowPage from './components/users';
import UpdatePostModal from './components/posts/UpdatePostModal';

const store = configureStore();
if (process.env.NODE_ENV !== 'production') {
  window.store = store;
  window.csrfFetch = csrfFetch;
  window.sessionActions = sessionActions;
  window.postActions = postActions;
  window.userActions = userActions;
}

const Root = () => {
  return (
    <Provider store ={store}>
        <BrowserRouter>
            <ModalProvider>
              <App />
              <Route path='/new'>
            <NewPost />
          </Route>
          <Route path='/new/text'>
            <NewTextModal />
          </Route>
          <Route path='/new/photo'>
            <NewPhotoModal />
          </Route>
          <Route path='/edit/:postid'>
            <UpdatePostModal />
          </Route>
          </ModalProvider>
        </BrowserRouter>
    </Provider>
  )
}
const renderApplication = () =>{
  ReactDOM.render(
    <React.StrictMode>
      <Root />
    </React.StrictMode>,
    document.getElementById('root')
    );
  }

if (sessionStorage.getItem("X-CSRF-Token")===null ||
    sessionStorage.getItem("currentUser")===null ){
  store.dispatch(sessionActions.restoreSession()).then(renderApplication);
  } else{
  renderApplication();
}
