    let location = useLocation();
    let background = location.state && location.state.background
    return(
        <>
        <div>
            <Link to='/new' className='post-button'>
                <i className="fa-sharp fa-solid fa-pencil" style={{color:'#696969'}}></i>
            </Link>

        </div>
        </>
    )
}
