import './navigation.css'
import { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom'
import {useDispatch,useSelector} from 'react-redux'
import * as sessionActions from '../../store/session';
import LoginFormModel from '../LoginFormModal';
const Profilebutton = () =>{
// work on dropdown menu that will bring down menu
const user = useSelector(state=>state.session.user)
const dispatch = useDispatch();
const [showMenu,setShowMenu] = useState(true);
let menu;
const openMenu = () => {
    if (showMenu) return;
    setShowMenu(true);
}

useEffect(()=>{
    if (!showMenu) return;
    const closeMenu=()=>{
        setShowMenu(false);
    };
    document.addEventListener('click',closeMenu);
    return ()=> document.removeEventListener("click",closeMenu);
},[showMenu]);

const logout=(e) => {
    e.preventDefault();
    dispatch(sessionActions.logout());
};

let sessionLinks;
console.log(user);
if(user){
    sessionLinks=(
        <>
        <li>{user.username}</li>
          <li>{user.email}</li>
          <li>
            <button onClick={logout} style={{ backgroundColor: 'transparent', border: 'none', boxShadow: 'none' }}>Log Out</button>
          </li>
        </>
    );
} else {
    sessionLinks=(
        <>
            {/* will trigger a modal when it is ready */}
            <button><LoginFormModel/></button>
            <NavLink to='/signup'>Sign In</NavLink>
        </>
    );
};

    return (
        <>
        <div>
        <button onClick={openMenu} style={{ backgroundColor: 'transparent', border: 'none', boxShadow: 'none' }}>
            <i className="fa-solid fa-user" style={{color:'#696969'}}></i>
        </button>
        {showMenu && (
            <ul className="profile-dropdown">
                {sessionLinks}
            </ul>
        )}
        </div>
        </>
    );
}

export default Profilebutton;
