import { useState } from "react"
import './index.css'
const EmailCheck = ({setEmail,onEmailClick,setEmailExists}) =>{
        const [initialEmail,setInitialEmail] = useState('');
        const [emailError,setEmailError] = useState('');

        const validateEmail = (email) => {
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            return emailRegex.test(email);
        }

        const handleEmailSubmit = (event) =>{
            event.preventDefault();

            setEmailError("That's not a valid email address. Please try again.")

        }
        // const handleEmailClick = (event) =>{
        //     setEmailError([]);

        //     if(!validateEmail(event.target.value)){
        //         emailError.push("That's not a valid email address. Please try again.");

        //     }
        // }
        return (
            <>
            <div className='email-box'>
                <div className='email-title'>mumblr</div>
                <div className='email-instructions'>Enter your email to login or register:</div>
                <form onSubmit={handleEmailSubmit}>
                <input className= 'email-text' type='text' placeholder='Username or Email' 
                    value={initialEmail} onChange={event=>setInitialEmail(event.target.value)} required
                    />

                    <button type='submit' onClick={event=>onEmailClick(event)} style={{ backgroundColor: 'transparent', border: 'none', boxShadow: 'none' }}>
                    next
                    </button>
                </form>
            </div>
                {emailError.map(error=> <div>{error}</div>)}
            </>
        )
}
export default EmailCheck;
