import './index.css'
import React,{useState,useEffect} from 'react';
import {useDispatch,useSelector} from 'react-redux'
import * as sessionActions from '../../store/session';
import { Redirect } from 'react-router-dom'
import csrfFetch from '../../store/csrf';
import EmailCheck from './EmailCheck';
const LoginForm = ()=>{
    const dispatch = useDispatch();
    // grab user info from current logged in user
    // most likely be blank
    const sessionUser = useSelector(state=> state.session.user);
    const [credential,setCredential] = useState('')
    const [password,setPassword] = useState('')
    const [errors,setErrors] = useState([]);
    const [emailCheck,setEmailCheck] = useState(false);
    if (sessionUser) return <Redirect to="/" />;
    const checkEmail = async (email) => {
        const response = await csrfFetch('./api/check_email',{
            method:"POST",
            body: JSON.stringify(email)
        });
        const exists = await response.json();
        return exists.exists;
    }

    const onEmailClick =(event)=>{
        setEmailCheck(checkEmail(event.target.value));
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        setErrors([]);

        return dispatch(sessionActions.login({credential, password}))
            .catch(async (res) => {
                let data;
                try {
                    data = await res.clone().json();
                } catch {
                    data = await res.text();
                }

                if (data?.errors) setErrors(data.errors);
                else if (data) setErrors([data]);
                else setErrors([res.statusText]);

            });
    }



    return(

            // <ul>
            //     {errors.map(error => <li key={error}>{error}</li>)}
            // </ul>
            setEmailCheck && <EmailCheck setEmail={setCredential} onEmailClick={onEmailClick}/>
    )
}

export default LoginForm;
